import React, { useState } from 'react'
import { useDispatch } from 'react-redux';
import { Button, Dropdown, Modal } from 'semantic-ui-react';
import { deleteTask } from '../../redux/slices/Task';

export default function ModalConfirmDeleteTask(props) {

    const [modalOpen, SetModalOpen] = useState(false);
    const dispatch = useDispatch();
  
    const handleOpen = (e) => SetModalOpen(true);
    const handleClose = (e) => SetModalOpen(false);
  
    const handleSubmit = (e) => {
     
  console.log(props.task._id);
      dispatch(deleteTask(props._id))
        .then((response) => {
          handleClose();
        
        })
        .catch((err) => {
          handleClose();
          throw err;
        });
    };
    return (
        <>
              <Modal
        trigger={
          <Dropdown.Item onClick={handleOpen} icon="delete" text="Delete" />
        }
        open={modalOpen}
        onClose={handleClose}
        dimmer="inverted"
        size="tiny"
      >
        <Modal.Header>{props.headerTitle}</Modal.Header>
        <Modal.Content>
          <p>
            Are you sure you want to delete{" "}
            <strong>{props.task.titre}</strong>?
          </p>
        </Modal.Content>
        <Modal.Actions>
          <Button
            onClick={handleSubmit}
            taskid={props.task._id}
            color="red"
          >
            Yes
          </Button>
          <Button onClick={handleClose} color="black">
            No
          </Button>
        </Modal.Actions>
      </Modal>
    </>
        
    )
}
