import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router';
import { Divider, Header, Icon, Image, Message, Segment } from 'semantic-ui-react';
import { getDetailByTaskByStudent } from '../../redux/slices/Grade';

export default function EndQuiz() {
const {id} = useParams()
const task = useSelector((state) => state.grades.grades)
    const  dispatch = useDispatch();
    useEffect(() => {
      dispatch(getDetailByTaskByStudent(id))
    
    }, [id])
    console.log(task);
    return (
        <div>
          { !task ? (<></>) : ( <>
          
            <Divider horizontal>
      <Header as='h4'>
        <Icon name='tag' />
        Result
      </Header>
    </Divider>

    <p>
      Doggie treats are good for all times of the year. Proven to be eaten by
      99.9% of all dogs worldwide.
    </p>

    <Divider horizontal>
      <Header as='h4'>
        <Icon name='bar chart' />
        Reponse
      </Header>
    </Divider>

    <Grid.Column>
                <Table key={index} color="red" textAlign="center">
                  <Table.Header>
                    <Table.Row>
                      <Table.HeaderCell>
                        {quiz.fquestion + "?"}{" "}
                      </Table.HeaderCell>
                    </Table.Row>
                  </Table.Header>

                  <Table.Body>
                    <Table.Row
                      positive={quiz.foptionA === quiz.correct_answer}
                      negative={quiz.foptionA !== quiz.correct_answer}
                    >
                      <Table.Cell>
                        <Icon name="check circle" color="green" />
                        {quiz.foptionA}
                      </Table.Cell>
                    </Table.Row>
                    <Table.Row
                      positive={quiz.foptionB === quiz.correct_answer}
                      negative={quiz.foptionB !== quiz.correct_answer}
                    >
                      <Table.Cell> {quiz.foptionB}</Table.Cell>
                    </Table.Row>
                    <Table.Row
                      positive={quiz.foptionC === quiz.correct_answer}
                      negative={quiz.foptionC !== quiz.correct_answer}
                    >
                      <Table.Cell> {quiz.foptionC}</Table.Cell>
                    </Table.Row>
                    <Table.Row
                      positive={quiz.foptionD === quiz.correct_answer}
                      negative={quiz.foptionD !== quiz.correct_answer}
                    >
                      <Table.Cell> {quiz.foptionD}</Table.Cell>
                    </Table.Row>
                  </Table.Body>
                </Table>
                <Divider hidden />
              </Grid.Column>
          </> )}


  
    
    </div>
  );

}
