import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { Button, Form, Message } from 'semantic-ui-react';
import { getTaskByTeacher, taskSlice, updateTask } from '../../redux/slices/Task';

export default function FormTask(props) {

    const [taskEdit , setTaskEdit] = useState(props.task)
    const  dispatch = useDispatch();
  
    const handleUpdate = () =>{
        dispatch(updateTask(taskEdit));
    }
    useEffect(() => {
        dispatch(getTaskByTeacher(taskEdit._id));
      }, [dispatch]);
    return (
        <div>
             <Form  onSubmit={handleUpdate}>
        <Form.Input
          label="Titre"
          type="text"
        
          name="Titre"
          maxLength="40"
          required
          value={taskEdit.title}
          onChange={(e) =>
            setTaskEdit({ ...taskEdit, title: e.target.value })
          }
        />
        <Form.TextArea
          label="Description"
          type="TextArea"
       
          name="Description"
          maxLength="5000"
          required
          value={taskEdit.description}
          onChange={(e) =>
            setTaskEdit({ ...taskEdit, description: e.target.value })
          }
        />
        <Message
          success
          color="green"
          header="Nice one! ðŸ“’ ðŸ“• ðŸ“š ðŸ“–"
        
        />
        <Message
          warning
          color="yellow"
          header="Woah! ðŸ˜± ðŸ˜¨"
        
        />
        <Button color="green" floated="right">
          Save
        </Button>
        <br /> {/* Yikes! Deal with Semantic UI React! */}
      </Form>
        </div>
    )
}
