import axios from "axios";
import React, { useState } from "react";
import Dropzone from "react-dropzone-uploader";
import { useDispatch, useSelector } from "react-redux";
import {
  Button,
  Dropdown,
  Feed,
  Form,
  Grid,
  Header,
  Icon,
  Image,
  List,
  Modal,
} from "semantic-ui-react";
import * as Yup from "yup";
import { useFormik } from "formik";
import FileUploadEdit from "../../utlis/FileUploadEdit";
import { isAuth } from "../../helpers/auth";
import {  getDetailByTaskByStudent, rendreTask } from "../../redux/slices/Grade";
import { UpdateProfilePicture } from "../../redux/slices/User";

export default function ModalTask({task}) {
  const Resources = useSelector((state) => state.tasks.files);

  const [open, setOpen] = React.useState(false);

  const dispatch = useDispatch();

  const [Images, setImages] = useState([]);
  const [enableUpload, setEnableUpload] = useState(false);

  const [up, setUp] = useState(0);

  const updateImages = (newImages) => {
    if (newImages === null) {
      task.listReponse.forEach((element) => {
        setImages(element);
      });
    } else {
      setImages(Images.concat(newImages));
    }
  };




    const formik = useFormik({
      initialValues: {
        _id : task._id,
        grade: task.grade,
        taskStatus: task.taskStatus,
      student : task.student,
        task: task.task,
        listReponse: [],


      },


      onSubmit: async (values) => {

        try {
          console.log("cc");
          console.log(Images);
          if(Images.length !==0 )
         { values.listReponse = Images;
          alert("images : here : "+values.listReponse);
        }
        else{
          values.listReponse = task.listReponse;
        }
        setEnableUpload(true)
        console.log(values);
        const res = await dispatch(rendreTask(values)).then(()=>{
          dispatch(getDetailByTaskByStudent(task.task._id));
      });
        } catch (error) {
          alert(error);
        }
      },
    });
  const closeModel = () => {



    setOpen(false);
  };



  return (
    <div>



<Modal
        onClose={() => setOpen(false)}
        onOpen={() => setOpen(true)}
        open={open}
        trigger={
          <div className='ui two buttons'>
          <Button 
          icon="cloud upload"
          >
            <Icon name="cloud upload"></Icon>
              upload File
            </Button>
            </div>

        }
      >
        <Modal.Header>Select a Photo</Modal.Header>
        <Modal.Content>
          <Modal.Description>
          <Form  onSubmit={formik.handleSubmit}>
        <Form
          label="Titre"
          type="text"

          name="title"
          maxLength="40"

          required

          onChange={
            formik.handleChange}
            value={formik.values.grade}
        />
        <Form.TextArea
          label="Description"
          type="TextArea"

          name="description"


          onChange={
            formik.handleChange}
            value={formik.values.taskStatus}
        />



            <Button type="submit" color="green" floated="right">
          Save
        </Button>
          <FileUploadEdit
            refreshFunction={updateImages}
            listfile={task.listReponse}  
          />
</Form>
          </Modal.Description>
        </Modal.Content>
        <Modal.Actions>
          <Button color="black" onClick={() => setOpen(false)}>
            Discard
          </Button>
          <Button
            content="Yep, Save"
            labelPosition="right"
            icon="checkmark"
            onClick={closeModel}
            color="red"
          />
             <br></br>

        </Modal.Actions>

      </Modal>




    </div>
  );
}
