import moment from "moment";
import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Link } from "react-router-dom";

import {
  Button,
  Statistic,
  Icon,
  Divider,
  Item,

  Header,
  Segment,
  Grid,
  Dropdown,

} from "semantic-ui-react";
import { isAuth } from "../../helpers/auth";

import {
  getNbrTasksMissing,
  getNbrTasksRemis,
  getTaskByTeacher,
  updateTaskStatus,

  assignAfterSave,
} from "../../redux/slices/Task";
import ModalConfirmDeleteTask from "./ModalConfirmDeleteTask";
import ModalUpdateTask from "./ModalUpdateTask";

export default function DisplayTaskTeacher() {
  const tasks = useSelector((state) => state.tasks.tasks);
  const remis = useSelector((state) => state.tasks.nbrRemis);
  const missing = useSelector((state) => state.tasks.nbrMissing);
  const currentClass = JSON.parse(localStorage.getItem("idClass"));
  const dispatch = useDispatch();
  const taskDetail={
    "idUser":isAuth()._id,
    "idClass" : currentClass._id,

  }
  const onSubmitAssignTask = (task) => {
    // dispatch(assignAfterSave(task));
    dispatch(updateTaskStatus(task)).then(() => {
      dispatch(assignAfterSave(task)).then(() => {
        dispatch(getTaskByTeacher(taskDetail));
      });
    });
  };


  const getItemInfo = (id) => {
    dispatch(getNbrTasksMissing(id));
    dispatch(getNbrTasksRemis(id));
  };
  useEffect(() => {
    dispatch(getTaskByTeacher(taskDetail));
  }, [dispatch]);
  return (
    <>
      <Divider hidden />

      <Header as="h2" content="List Task" />

      <Divider />

      {tasks.length <=0  ? (
        <Segment placeholder>
          <Header icon>
            <Icon name="tasks" />
            No Tasks Added.
            <Link to="/AddTask">
              <Button color='google plus' >
           Add Task
              </Button>

            </Link>
          </Header>
        </Segment>
      ) : (
        tasks.map((task, index) => (
         <>
            <Segment color="grey" raised>
              <Item.Group divided key={index}>

                <Item>

                  <Item.Image
                    size="tiny"
                    avatar
                    src={process.env.PUBLIC_URL + "/quiz.png"}
                  />

                  <Item.Content>
                  <Link to={"/DetailTask/" + task._id}>
                    <Item.Header as="a">{task.title}</Item.Header>
                    </Link>
                    <Item.Meta>
                      <span className="cinema">
                        {moment(task.endDate).format("MMMM Do yy")}
                      </span>
                    </Item.Meta>
                    <Item.Description>{task.description}</Item.Description>
                  </Item.Content>

                  <Grid columns={4}>

                    <Grid.Row >

                      <Statistic.Group size="small">


                        <Statistic color="red">
                          <Statistic.Value>
                            {getItemInfo.call(this, task._id)}
                            {missing}
                          </Statistic.Value>

                          <Statistic.Label>Missing</Statistic.Label>
                        </Statistic>

                        <Grid.Column>
                          <Statistic color="green">
                            <Statistic.Value>
                              {getItemInfo.call(this, task._id)}
                              {remis}
                            </Statistic.Value>
                            <Statistic.Label>Done</Statistic.Label>
                          </Statistic>
                        </Grid.Column>
                      </Statistic.Group>
                      <Grid.Column></Grid.Column>
                      <Grid.Column>  
                       <Dropdown
                              fluid
                              pointing
                              direction="left"
                              className="icon"
                              icon="ellipsis vertical"
                            >
                              <Dropdown.Menu>
                                <ModalUpdateTask
                                  headerTitle="Delete Task"
                                  buttonTriggerTitle="Delete"
                                  buttonColor="red"
                                  icon="trash"
                                  task={task}
                                >


                                </ModalUpdateTask>
                              <ModalConfirmDeleteTask
                                  headerTitle="Delete Task"
                                  buttonTriggerTitle="Delete"
                                  buttonColor="red"
                                  icon="trash"
                                  task={task}

                                />
                              </Dropdown.Menu>
                            </Dropdown></Grid.Column>
                    </Grid.Row>

                    <Grid.Row>
                      <Grid.Column>
                        {task.status === "not assign" ? (
                          <Button
                            color="red"
                            type="submit"
                            onClick={() => onSubmitAssignTask(task)}
                          >
                            Assign
                          </Button>
                        ) : (
                          <></>
                        )}
                      </Grid.Column>

                    </Grid.Row>
                  </Grid>
                </Item>
              </Item.Group>
            </Segment>

            <Divider hidden></Divider>
          </>
        ))
      )}
    </>
  );
}
