  Button,
  Dropdown,
  Feed,
  Grid,
  Header,
  Icon,
  Image,
  List,
  Modal,
} from "semantic-ui-react";
import FileUploadEdit from "../../utlis/FileUploadEdit";
import { isAuth } from "../../helpers/auth";
import { DeleteResources, UpdateResources, uploadFile } from "../../redux/slices/Task";
