import React, { useCallback, useEffect } from "react";
import styled from "styled-components";
import useMediaRecorder from "@wmik/use-media-recorder";
import { useDispatch } from "react-redux";
import axios from "axios";
import { isAuth } from "../../helpers/auth";
import { AddCourses } from "../../redux/slices/Courses";

import { Button, Header, Icon, Modal } from "semantic-ui-react";
import ListUsers from "./ListUsers";

const BottomBar = ({
  clickChat,
  goToBack,
  toggleCameraAudio,
  userVideoAudio,
  clickScreenSharing,
  screenShare,
  videoDevices,
  showVideoDevices,
  setShowVideoDevices,
  listuserRoom,
}) => {
  const handleToggle = useCallback(
    (e) => {
      setShowVideoDevices((state) => !state);
    },
    [setShowVideoDevices]
  );

  function Player({ srcBlob, audio }) {
    if (!srcBlob) {
      return null;
    }

    if (audio) {
      return <audio src={URL.createObjectURL(srcBlob)} controls />;
    }

    return (
      <video
        src={URL.createObjectURL(srcBlob)}
        width={520}
        height={480}
        controls
      />
    );
  }

  let {
    error,
    status,
    mediaBlob,
    stopRecording,
    getMediaStream,
    startRecording,
  } = useMediaRecorder({
    recordScreen: true,
    blobOptions: { type: "video/mp4" },
    mediaStreamConstraints: { audio: true, video: true },
    mediaRecorderOptions: {
      audioBitsPerSecond: 128000,
      videoBitsPerSecond: 2500000,
    },
  });
  const dispatch = useDispatch();
  const [open, setOpen] = React.useState(false);
  const CurrentClass = JSON.parse(localStorage.getItem("idClass"));
  useEffect(() => {
    if (!mediaBlob) {
      return;
    }

    console.log(mediaBlob);
    const idSeance = "";
    const today = Date.now();
    const titre =
      "Recorded Session of : " +
      new Intl.DateTimeFormat("en-US", {
        year: "numeric",
        month: "2-digit",
        day: "2-digit",
        hour: "2-digit",
        minute: "2-digit",
        second: "2-digit",
      }).format(today);
    const description =
      "this is a recorded session was added as a course automaticly beacause your teacher has recorded the lesson so you can access to this video .";
    const multiple_resources = [];
    multiple_resources.push(mediaBlob);
    const idOwner = isAuth()._id;
    const idClass = CurrentClass._id;
    dispatch(
      AddCourses(
        idSeance,
        titre,
        description,
        multiple_resources,
        idOwner,
        idClass
      )
    ).then((res) => {
      console.log(res.data);
    });
  }, [mediaBlob]);
 
  return (
    <Bar>
      <Left>
      <ListUsers  userlistromm={listuserRoom}/>
        <CameraButton onClick={toggleCameraAudio} data-switch="video">
          <div>
            {userVideoAudio.video ? (
              <FaIcon className="fas fa-video"></FaIcon>
            ) : (
              <FaIcon className="fas fa-video-slash"></FaIcon>
            )}
          </div>
          Camera
        </CameraButton>
        {showVideoDevices && (
          <SwitchList>
            {videoDevices.length > 0 &&
              videoDevices.map((device) => {
                console.log(device);
                return <div>{device.label}</div>;
              })}
            <div>Switch Camera</div>
          </SwitchList>
        )}
        <SwitchMenu onClick={handleToggle}>
          <i className="fas fa-angle-up"></i>
        </SwitchMenu>
        <CameraButton onClick={toggleCameraAudio} data-switch="audio">
          <div>
            {userVideoAudio.audio ? (
              <FaIcon className="fas fa-microphone"></FaIcon>
            ) : (
              <FaIcon className="fas fa-microphone-slash"></FaIcon>
            )}
          </div>
          Audio
        </CameraButton>

      </Left>
      <Center>
        <ChatButton onClick={clickChat}>
          <div>
            <FaIcon className="fas fa-comments"></FaIcon>
          </div>
          Chat
        </ChatButton>
        <StartButton onClick={startRecording} disabled={status === "recording"}>
          <div>
            <FaIcon className="fas fa-play-circle"></FaIcon>
          </div>
          Start
        </StartButton>
        {/* Modal of info when record has been stoped */}
        <Modal
          onClose={() => setOpen(false)}
          onOpen={() => setOpen(true)}
          open={open}
          trigger={
            <StopRecordButton
              onClick={stopRecording}
              disabled={status !== "recording"}
            >
              <div>
                <FaIcon className="fas fa-stop-circle"></FaIcon>
              </div>
              Stop
            </StopRecordButton>
          }
        >
          <Header icon="record" content="Information about Record Session" />
          <Modal.Content>
            <p>
              After this session got recorded it will be added automatically as
              a new courses in your class timeline and it will take as a title
              "Recorded Session of today"
            </p>
          </Modal.Content>
          <Modal.Actions>
            <Button color="red" onClick={() => setOpen(false)}>
              <Icon name="remove" /> ok
            </Button>
          </Modal.Actions>
        </Modal>

        {/* Modal of info when record has been stoped */}

        <ScreenButton onClick={clickScreenSharing}>
          <div>
            <FaIcon
              className={`fas fa-desktop ${screenShare ? "sharing" : ""}`}
            ></FaIcon>
          </div>
          Share Screen
        </ScreenButton>
        <a href="http://localhost:3000/WhiteBoard" target="_blank"
                                      rel="noopener noreferrer" >
           <WhiteBoard>
           <div>
            <FaIcon
              className={'fas fa-chalkboard-teacher'}
            ></FaIcon>
          </div>
          WhiteBoard
        </WhiteBoard>
        </a>
      </Center>

      <Right>
        <StopButton onClick={goToBack}>Stop</StopButton>
      </Right>
    </Bar>
  );
};

const Bar = styled.div`
  position: absolute;
  right: 0;
  bottom: 0;
  width: 100%;
  height: 8%;
  display: flex;
  justify-content: center;
  align-items: center;
  font-weight: 500;
  background-color: red;
`;
const Left = styled.div`
  display: flex;
  align-items: center;

  margin-left: 15px;
`;
const WhiteBoard = styled.div`
width: 75px;
border: none;
font-size: 0.9375rem;
padding: 5px;

:hover {
  background-color: #77b7dd;
  cursor: pointer;
  border-radius: 15px;
}

* {
  pointer-events: none;
}
`;
const Center = styled.div`
  flex: 1;
