import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { Button, Form, Message } from 'semantic-ui-react';
import { taskSlice, updateTask } from '../../redux/slices/Task';

export default function FormTask(props) {

    const [taskEdit , setTaskEdit] = useState(props.task)
    const  dispatch = useDispatch();
    const handleTitleChanges = (e)=>{
        setTaskEdit(...taskEdit.title = e.target.value)

    }
    const handleUpdate = () =>{
        dispatch(updateTask(props.task));
    }
   
    return (
        <div>
             <Form onSubmit={handleUpdate} >
        <Form.Input
          label="Titre"
          type="text"
          placeholder="Seance 1"
          name="Titre"
          maxLength="40"
          required
          value={props.task.title}
          onChange={handleTitleChanges}
        />
        <Form.TextArea
          label="Description"
          type="TextArea"
          placeholder="In this workshop we will learn ..."
          name="Description"
          maxLength="5000"
          required
          value={props.task.description}
    
        />
        <Message
          success
          color="green"
          header="Nice one! ðŸ“’ ðŸ“• ðŸ“š ðŸ“–"
        
        />
        <Message
          warning
          color="yellow"
          header="Woah! ðŸ˜± ðŸ˜¨"
        
        />
        <Button color="green" floated="right">
          Save
        </Button>
        <br /> {/* Yikes! Deal with Semantic UI React! */}
      </Form>
        </div>
    )
}
