import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router';
import { Grid, Statistic } from 'semantic-ui-react'
import {  getNbrTasksMissing, getNbrTasksRemis } from '../../redux/slices/Task';

export default function StateTask(props) {
    const remis = useSelector((state) => state.tasks.nbrRemis);
 
    const missing = useSelector((state) => state.tasks.nbrMissing);
    const {id} = useParams();
    const  dispatch = useDispatch();
    useEffect(() => {
     
        dispatch(getNbrTasksMissing(id));
        dispatch(getNbrTasksRemis(id))
      
      }, [id])
    return (
        <div>
               <Statistic.Group size="small">
                        
                        <Grid.Column>
                            <Statistic color="black">
                              <Statistic.Value>
                            {props.student}
                              </Statistic.Value>
                              <Statistic.Label>Students</Statistic.Label>
                            </Statistic>
                          </Grid.Column>
                          <Statistic color="red">
                            <Statistic.Value>
                         
                              {props.missing}
                            </Statistic.Value>
  
                            <Statistic.Label>Missing</Statistic.Label>
                          </Statistic>
                 
                          <Grid.Column>
                            <Statistic color="green">
                              <Statistic.Value>
                        
                                {props.remis}
                              </Statistic.Value>
                              <Statistic.Label>Done</Statistic.Label>
                            </Statistic>
                          </Grid.Column>
                        </Statistic.Group>
        </div>
    )
}
