import React, { useState } from "react";

import { Multiselect } from "multiselect-react-dropdown";
import Select from "react-select";
import SemanticDatepicker from "react-semantic-ui-datepickers";

import {
  Dropdown,
  Button,
  Form,
  Grid,
  Rail,
  Segment,
  TextArea,
  Label,
  Modal,
} from "semantic-ui-react";
import { useSelector } from "react-redux";
import { isAuth } from "../../helpers/auth";

export default function ModalTaskFile(props) {

    const currentClass = JSON.parse(localStorage.getItem("idClass"));
    const seances = useSelector((state) => state.seance.seance);
    const [studentChosen] = useState(currentClass.classUsers);
    const [theme, setTheme] = useState([]);
   
    const [tasks , setTask] = useState({
   
        title : "",
        description : "",
        theme : "",
        cour : currentClass._id,
        typeTask : "",
        listQuestion : [],
        listStudents : [],
        endDate : null,
        creator : isAuth()._id
     
    });

      const event = () => {
        props.addTask(tasks);
        console.log(tasks.listStudents);
    
     
      };
    
      const onSelect = (selectedList, selectedItem) => {
        console.log(selectedItem);
        setTask(tasks.listStudents.push(selectedItem));
    
        console.log(tasks.listStudents);
      };
      const onRemove = (selectedList, removedItem) => {
        setTask(
          (tasks.listStudents = selectedList.filter(
            (item) => item._id !== removedItem._id
          ))
        );
    
        console.log(tasks.listStudents);
      };
    
      const selectedTheme = (selectedList, selectedItem) => {
        console.log(selectedItem);
        setTheme(selectedItem);
        setTask((tasks.theme = selectedItem._id));
      };

    return (
        <div>
               <Modal
        trigger={<Dropdown.Item icon="file text" text="Add Task" />}
        dimmer="inverted"
        size="tiny"
        closeIcon="close"
      >
        <Modal.Header>{props.headerTitle}</Modal.Header>
        <Modal.Content>
        <Grid>
          <Grid.Row>
            <Grid.Column width={11}>
              <Form >
                <Form.Field>
                  <Form.Input
                    label="Title"
                    required
                   
                    onChange={(e) =>
                      setTask({ ...tasks, title: e.target.value })
                    }
                    placeholder="Title"
                  />
                </Form.Field>
                <Form.Field required>
                  <label>Description</label>
                  <TextArea
                  required
                    label="Description"
              
                    onChange={(e) =>
                      setTask({ ...tasks, description: e.target.value })
                    }
                    placeholder="Description.."
                    style={{ minHeight: 50 }}
                  />
                </Form.Field>
                <Form.Field>
                  <label>Due</label>
                  <SemanticDatepicker
                    onChange={(e, data) =>
                      setTask({ ...tasks, endDate: data.value })
                    }
                    value={tasks.endDate}
                  />
                </Form.Field>
              </Form>
            </Grid.Column>
            <Grid.Column width={5}>
              <label>For : </label>
              <Multiselect
              required
                placeholder="Select seance"
                style={{
                  chips: { background: "red" },
                  option: { color: "black" },
                  searchBox: {
                  
                    border: "none",
                  },
                  chips: { // To change css chips(Selected options)
                    background: "red"
                    }
                }}
                onSelect={selectedTheme}
                fluid
                options={seances}
                selection
                singleSelect={true}
                hidePlaceholder
                displayValue="titre"
              
              />
              <br/>
              <Form.Field required>
                           <label>For : </label>
              <Multiselect
                placeholder="Select Student"
                style={{
                  chips: { background: "red" },
                  option: { color: "black" },
                  searchBox: {
                    // To change search box element look
                    border: "none",
                  },
                  chips: { // To change css chips(Selected options)
                    background: "red"
                    }
                }}
                loadingMessage
                showArrow
                fluid
                selection
                multiple
                displayValue="name"
                options={studentChosen}
                selectedValues={tasks.listStudents}
                onSelect={onSelect}
                onRemove={onRemove}
                hidePlaceholder
              />
              </Form.Field>
            </Grid.Column>
          </Grid.Row>
        </Grid>
        </Modal.Content>
      </Modal>
        </div>
    )
}
