import React from 'react'
import { Button, Header, Icon, Modal } from 'semantic-ui-react'

export default function ModalAssignTask(props) {
    const [open, setOpen] = React.useState(false)
    const assign = ()=>{
        dispatch(updateTaskStatus(taskk));
      dispatch(assignAfterSave(taskk));
        dispatch(getTaskByTeacher(taskDetail));
        setOpen(false)
    }
    return (
        <div>
              <Modal
      
      onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
      open={open}
      size='small'
      trigger={<Button>Assign</Button>}
    >
      <Header >
        <Icon name='archive' />
        Assign Task
      </Header>
      <Modal.Content>
        <p>
          {props.task.description}
        </p>
      </Modal.Content>
      <Modal.Actions>
        <Button  color='red'  onClick={() => setOpen(false)}>
          <Icon name='remove' /> No
        </Button>
        <Button color='black'  onClick={assign}>
          <Icon name='checkmark' /> Yes
        </Button>
      </Modal.Actions>
    </Modal>
        </div>
    )
}
